import {Button} from "@mui/material";
import axios from "axios";
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Item.css";
const Item = (props) => {
    const history = useNavigate ();
    const { _id,name,author,description,price,image} = props.item;
     const deleteHandler = () => {
     axios
          .delete('http://localhost:5000/items/${_id}')
          .then((res) => res.data)
          .then(()=> history("/item"));   
     };
     return (
     <div className = "card" >
         <img src={image} alt={name} />
         <article>By {author} </article>
         <h3> {name} </h3>
          <p>{description} </p>
          <h3>Rs{price} </h3>
          <Button LinkComponent={Link} to={'/items/${_id}'} sx={{ mt: "auto"}} >
            Update
            </Button>
          <Button onClick={deleteHandler} sx={{ mt: "auto"}} >Delete</Button>
    </div>
  );
};

export default Item;
