import { Button } from "@mui/material";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Item.css";

const Item = (props) => {
  const history = useNavigate();
  const { _id, name, category, description, price, image } = props.item;

  const deleteHandler = async () => {
    await axios
      .delete(`http://localhost:5000/items/${_id}`)
      .then((res) => res.data)
      .then(() => history("/"))
      .then(() => history("/items"));
  };

  const generatePDF = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/items`);
      const items = response.data.items;

      const doc = new jsPDF();
      doc.text("Added Items", 10, 10);
      let y = 20;

      items.forEach((item, index) => {
        doc.text(`${index + 1}. ${item.name} - ${item.price}`, 10, y);
        y += 10;
      });

      doc.save("added-items.pdf");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="card">
      <img src={image} alt={name} />
      <article> {category} </article>
      <h3> {name} </h3>
      <p>{description} </p>
      <h3>Rs. {price} up </h3>
      <Button LinkComponent={Link} to={`/items/${_id}`} sx={{ mt: "auto" }}>
        Update
      </Button>
      <Button color="error" onClick={deleteHandler} sx={{ mt: "auto" }}>
        Delete
      </Button>
      <Button onClick={generatePDF} sx={{ mt: "auto" }}>
        Generate PDF
      </Button>
    </div>
  );
};

export default Item;
