import { Box, Button, Tab, Tabs, TextField, Toolbar, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import jsPDF from "jspdf";
import "./css/Home.css";
import "./Item/Item.css";
import axios from "axios";
import logo from "./img/logo2.png";
import Item from "./Item/Item";
import UItem from "./Item/UItem";


const URL = "http://localhost:5000/items";

const fetchHandler = async() => {
  return await axios.get(URL).then((res)=> res.data);
};

const Home = () => {
  const [query, setQuery] = useState('');
  const [items, setItems] = useState([]);

  useEffect(() => {
     fetchHandler().then((data) => setItems(data.items));
  }, []);

  const handleSearch = () => {
    axios.get(`${URL}?q=${query}`)
      .then(res => setItems(res.data.items))
      .catch(err => console.log(err));
  };

  const generatePDF = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/items`);
      const items = response.data.items;

      const doc = new jsPDF();
      doc.text("Added Items", 10, 10);
      let y = 20;

      items.forEach((item, index) => {
        doc.text(`${index + 1}. ${item.name} - ${item.price}`, 10, y);
        y += 10;
      });

      doc.save("added-items.pdf");
    } catch (error) {
      console.log(error);
    }
  };



  return (
    <div>
      <div className='top-tittlee'>
        <div> <a href = "/Items"> <img src = {logo}/> </a></div>
        <Toolbar>
      <div className='navi'>
        <a href = "">Home</a>
        <a href = "">Services</a>
        <a href = "">About Us</a>
        <a href = "">Contact Us</a>
      </div>
    </Toolbar>




      </div>



      <br/>
 
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: '1rem' }}>
        <TextField label="Search" variant="outlined" value={query} onChange={(e) => setQuery(e.target.value)} sx={{ mr: '1rem' }} />
        <Button variant="contained" onClick={handleSearch}>Search</Button>
      </Box>

      <Button variant="contained" color="purple">Red Button</Button>

      <Button onClick={generatePDF} sx={{ mt: "auto" }}>
        Price List
      </Button> 

      <div>
        <ul>
          {items && items.map((item, i) => (
            <li key={i}>
              <UItem item={item} />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
