import { useState } from "react";

const Item = (props) => {
  const [searchQuery, setSearchQuery] = useState("");

  const { _id, name, category, description, price, image } = props.item;

  const deleteHandler = async () => {
    await axios
      .delete(`http://localhost:5000/items/${_id}`)
      .then((res) => res.data)
      .then(() => history("/"))
      .then(() => history("/items"));
  };

  const filtered = props.items.filter((item) => {
    return item.name.toLowerCase().includes(searchQuery.toLowerCase());
  });

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {filtered.map((item) => (
        <div key={item._id}>
          <img src={item.image} alt={item.name} />
          <article>{item.category}</article>
          <h3>{item.name}</h3>
          <p>{item.description}</p>
          <h3>Rs. {item.price} up</h3>
          <Button
            LinkComponent={Link}
            to={`/items/${item._id}`}
            sx={{
              bgcolor: "black",
              mt: "auto",
              color: "white",
              "&:hover": { bgcolor: "grey" },
            }}
            color="primary"
          >
            Update
          </Button>

          <Button
            color="error"
            onClick={() => deleteHandler(item._id)}
            sx={{
              bgcolor: "red",
              mt: "auto",
              color: "white",
              "&:hover": { bgcolor: "orange" },
            }}
          >
            Delete
          </Button>
        </div>
      ))}
    </div>
  );
};

export default Item;
