import { Box, Button, Tab, Tabs, TextField, Toolbar, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import jsPDF from "jspdf";
import "./css/Home.css";
import "./Item/Item.css";
import axios from "axios";
import logo from "./img/logo2.png";
import Item from "./Item/Item";
import UItem from "./Item/UItem";


const URL = "http://localhost:5000/items";

const fetchHandler = async() => {
  return await axios.get(URL).then((res)=> res.data);
};

const Home = () => {
  const [query, setQuery] = useState('');
  const [items, setItems] = useState([]);

  useEffect(() => {
     fetchHandler().then((data) => setItems(data.items));
  }, []);

  const handleSearch = () => {
    axios.get(`${URL}?q=${query}`)
      .then(res => setItems(res.data.items))
      .catch(err => console.log(err));
  };

  exportPDF= () => {
      const unit = "pt";
        const size = "A4"; // Use A1, A2, A3 or A4
        const orientation = "portrait"; // portrait or landscape
    
        const marginLeft = 40;
        const doc = new jsPDF(orientation, unit, size);
    
        doc.setFontSize(15);
    
        const title = "Jacklup Solutions Price List";
        const headers = [["Id","Name", "Category","Price"]];
    
        const data = this.state.feedback.map(elt=> [elt._id, elt.Name, elt.category, elt.price]);
    
        let content = {
          startY: 50,
          head: headers,
          body: data
        };
    
        doc.text(title, marginLeft, 40);
        doc.autoTable(content);
        doc.save("Price List.pdf")
    }
  


render() 
  return (
    <div>
      <div className='top-tittlee'>
        <div> <a href = "/Items"> <img src = {logo}/> </a></div>
        <Toolbar>
      <div className='navi'>
        <a href = "">Home</a>
        <a href = "">Services</a>
        <a href = "">About Us</a>
        <a href = "">Contact Us</a>
      </div>
    </Toolbar>




      </div>



      <br/>
 
      <Box sx={{ display: 'flex', alignItems: 'center',gap:5, justifyContent: 'center', mb: '1rem' }}>
        <TextField label="Search" variant="outlined" value={query} onChange={(e) => setQuery(e.target.value)} sx={{ mr: '1rem' }} />
        <Button variant="contained" onClick={handleSearch}>Search</Button>
        
           <button className='btn btn-primary' onClick={() => this.exportPDF()} style={{marginLeft:800}}>Export Result</button>
      
      </Box>

     


      

      <div>
        <ul>
          {items && items.map((item, i) => (
            <li key={i}>
              <UItem item={item} />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Home;
